package p0614;

public class C0614_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("hello world!");
		
		//boolean - 논리형 true or false, 소문자로 사용가능
		boolean bool1 = true; 
		boolean bool2 = false;
		
		//문자형 - 한 글자만 가능
		char ch1 = 'a';
		char ch2 = 'B';
		char ch3 = ' '; // 빈 공백은 저장 가능 ''는 불가능
		
		// 정수형 - byte, short, (int),long : L접미사를 붙여야 함
		//byte a = 128;
		short b = 2;
		int c = 217483648;
		long d = 2147483648L;
		
		// 실수형 float, double - F, d 를 붙여야 함 double은 생략 가능
		float e = 3.14F;
		double f = 3.14;
	}

}
